import React,{useEffect, useState} from "react";
import { BrowserRouter, Route, Switch } from "react-router-dom";
import { connect } from "react-redux";

import "./App.css";
import LandingPage from "./screens/landingPage";
import ScrollToTop from "./components/ScrollToTop";
import Timeline from "./screens/timeline/Timeline";
import ForgotPassword from "./screens/ResetPassword";

import SinglePost from "./screens/timeline/singlepost";

function App(props) {
  const [loggedin,setLoggedIn]=useState();
  props.data && (<>localStorage.setItem("id",props.data.id)
  localStorage.setItem("username",props.data.username)
  </>)
  // localStorage.setItem("id")
  useEffect(()=>{
    if(localStorage.getItem("id") ){
      setLoggedIn(true);
    }else{
      setLoggedIn(false)
    }
  },[props.isLoggedIn])

  return (
    <BrowserRouter>
      <ScrollToTop>
        <Switch>
          <Route exact path="/reset/:token" component={ForgotPassword} />
          { loggedin ? (
            <Switch>
              <Route exact path="/" component={Timeline} />
              <Route exact path="/:id" component={SinglePost}/>
            </Switch>
          ) : (
            <Route exact path={["/login", "/forgot", "/"]}>
              <LandingPage />
            </Route>
          )}
        </Switch>
      </ScrollToTop>
    </BrowserRouter>
  );
}
function mapStateToProps(state) {
  console.log("map state is",state)
  return {
    data:state.data,
    isLoggedIn: state.isLoggedIn,
  };
}
export default connect(mapStateToProps)(App);
