import React from "react";
import { BrowserRouter, Route, Switch } from "react-router-dom";
import { useHistory } from "react-router-dom";
import { Redirect } from "react-router-dom";
import "./App.css";
import Landing_page from "./screens/landingPage";
import ScrollToTop from "./components/ScrollToTop";
import Timeline from "./screens/timeline/Timeline";
import Navbar from "./screens/components/navbar/Navbar";
import Footer from "./screens/components/footer/Footer";
import ForgotPassword from "./screens/ResetPassword";

import SinglePOst from "./screens/timeline/singlepost";

function App(props) {
  let history = useHistory();

  if(localStorage.getItem("id")==null && window.location.pathname!=="/" &&!window.location.pathname!=="/login"){
return <Redirect to="/" /> }
  //   if( window.location.pathname.slice(0,9)==="/timeline" && !localStorage.getItem("id")){
  // // window.location.href="google.com";
  // window.location.href="http://localhost:3000/login"
  // return null;

  return (
    <BrowserRouter>
      <ScrollToTop>
        <Switch>
          <Route exact path="/reset/:token" component={ForgotPassword} />
          {/* <Route exact path="/" component={Landing_page} /> */}
          {/* <Navbar /> */}
          <Switch>
            <Route exact path="/" component={Landing_page} />
            <Route path="/timeline" component={Timeline} />
            <Route path="/timeline/:id" component={SinglePOst} />
          </Switch>{" "}
        </Switch>
        {/* <Footer/> */}
      </ScrollToTop>
    </BrowserRouter>
  );
}
export default App;
