import React, { useState } from "react";
import { BrowserRouter, Route, Router, Switch } from "react-router-dom";
import { useHistory } from "react-router-dom";
import { Redirect } from "react-router-dom";
import "./App.css";
import Landing_page from "./screens/landingPage";
import ScrollToTop from "./components/ScrollToTop";
import Timeline from "./screens/timeline/Timeline";
import ForgotPassword from "./screens/ResetPassword";

import SinglePOst from "./screens/timeline/singlepost";

function App(props) {
  const [redirect, setRedirect] = useState(false);
  window.onload = () => {
    if (
      localStorage.getItem("id") == null &&
      window.location.pathname !== "/" &&
      !window.location.pathname !== "/login"
    ) {
      setRedirect(true);
    }
  };

  return (
    <BrowserRouter>
        {console.log(props)}

      <ScrollToTop>

          <Switch>
          <Route
                exact
                path="/timeline"
                render={() => {
                    return (
                      redirect?
                      <Redirect to="/timeline" /> :
                      <Redirect to="/" /> 
                    )
                }}
              />
            <Route exact path="/reset/:token" component={ForgotPassword} />

            <Switch>
              <Route exact path="/" component={Landing_page} />
              <Route path="/timeline" 
 component={Timeline} />
              <Route path="/timeline/:id" component={SinglePOst} />
            </Switch>{" "}
          </Switch>

        {/* <Footer/> */}
      </ScrollToTop>
    </BrowserRouter>
  );
}
export default App;
