import React, { useState } from "react";
import { BrowserRouter, Route, Switch } from "react-router-dom";
// import { Redirect } from "react-router-dom";
import { connect } from "react-redux";

import "./App.css";
import LandingPage from "./screens/landingPage";
import ScrollToTop from "./components/ScrollToTop";
import Timeline from "./screens/timeline/Timeline";
import ForgotPassword from "./screens/ResetPassword";

import SinglePOst from "./screens/timeline/singlepost";

function App(props) {
  const token = localStorage.getItem("id");
  console.log(token);
  return (
    <BrowserRouter>
      <ScrollToTop>
        <Switch>
          <Route exact path="/reset/:token" component={ForgotPassword} />
          {props.isLoggedIn && token != null ? (
            <Switch>
              <Route exact path="/" component={Timeline} />
              <Route exact path="/timeline/:id">
                <SinglePOst />
              </Route>
            </Switch>
          ) : (
            <Route exact path={["/login", "/forgot", "/"]}>
              <LandingPage />
            </Route>
          )}
          {/* {token==null ?UnauthorizedRoutes :AuthorizedRoutes} */}
        </Switch>
      </ScrollToTop>
    </BrowserRouter>
  );
}
function mapStateToProps(state) {
  console.log(state);
  return {
    isLoggedIn: state.isLoggedIn,
  };
}
export default connect(mapStateToProps)(App);
