import { put, takeLatest,call } from 'redux-saga/effects'
import axios from 'axios'


function getUData(action){
    return axios.post("http://localhost:3003/upload/getpost",{start:action.payload})
}
export function* getDataAsync(action) {
  try{
       const allData=  yield call(getData, action)
        yield put({ type: 'GETALLDATA',allData})
  }
  catch (error) {
    yield put({ type:"HANDLEERROR", error });
  }
}

export function* watchGetDataAsync(action) {
    yield takeLatest('REQUESTDATA', getDataAsync)
}
