import React from "react";
import { BrowserRouter, Route, Router, Switch } from "react-router-dom";

import "./App.css";
import Landing_page from "./screens/landing_page/Landing_page";
import ScrollToTop from "./components/ScrollToTop";
import Timeline from "./screens/timeline/Timeline";
import Login_home from "./screens/login_home/Login_home";
import SinglePOst from "./screens/timeline/singlepost";
import ForgotPassword from "./screens/ResetPassword";

function App() {
  if (
    window.location.pathname.slice(0, 9) === "/timeline" &&
    !localStorage.getItem("id")
  ) {
    // window.location.href="google.com";
    window.location.href = "http://localhost:3000/login";
    return null;
  }
  return (
    <BrowserRouter>
      <ScrollToTop>
        <Switch>
          <Route exact path="/reset/:token" component={ForgotPassword} />
          <Route path="/">
            {/* <Navbar /> */}
            <Switch>
              <Route path={["/login", "/signup"]}>
                {/* <Image /> */}
                <Switch>
                  <Route path="/signup" component={Landing_page} />
                  <Route path="/login" component={Login_home} />
                </Switch>
              </Route>
            </Switch>
            {/* <Footer /> */}
          </Route>
        </Switch>
        {/* <Switch>
          <Route exact path="/" component={Landing_page} />

          <Route path="/login" component={Login_home} />
          <Route exact path="/reset/:token" component={ForgotPassword} />

          <Route exact path="/timeline" component={Timeline} />
          <Route path="/timeline/:id" component={SinglePOst} />
        </Switch> */}
      </ScrollToTop>
      {/* <Footer /> */}
    </BrowserRouter>
  );
}
// camel case notation
export default App;

