import React, { Component, useEffect } from "react";
import { Link, withRouter } from "react-router-dom";
import Heading from "../../../../components/heading/Heading";
import axios from "axios";
import FormInput from "../../../../components/forms/Form";
import Url from "../../../../util/Config";
import ForgotPassword from "../forgotPassword/ForgotPassword"

useEffect
const [state, setstate] = useState(initialState)

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      error: "",
      check: false,
    };
  }
  //changing input field

  handleChange = (e) => {
    e.preventDefault();
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  //submiting data for login a user
  submit = (e) => {
    e.preventDefault();

    axios.post(`${Url.baseurl}/auth/`, this.state).then((response) => {
      if (!response.data._id) {
        this.setState({
          error: "Wrong Email or Password",
        });
      } else {
        localStorage.setItem("id", response.data._id); //setting id to localstorage
        localStorage.setItem("username", response.data.username); //setting username to localstorage

        this.setState({
          //setting up error
          error: "",
        });
        this.props.history.push("/timeline"); //push to timeline after successful login
      }
      console.log(response);
    });
  };

  //toggal forget password dialog box

  openForgetPassDialogBox = (e) => {
    e.preventDefault();
    this.setState((prevState) => ({
      check: !prevState.check,
    }));
  };

  render() {
    return (
      <div>
        <div className="content_rgt">
          <div className="login_sec">
            {this.state.check ? (
              <div>
              <Heading content="Forgot Password" size="36px" />
              <ForgotPassword openForgetPassDialogBox={this.openForgetPassDialogBox}/>
              </div>
            ) : (
              <div>
                <Heading content="Log In" size="36px" />
                <form onSubmit={this.submit}>
                  <ul>
                    <li>
                      {" "}
                      <FormInput //form component
                        label="Email"
                        name="email"
                        type="email"
                        placeholder="Enter email"
                        onchange={this.handleChange}
                      />
                    </li>
                    <li>
                      {" "}
                      <FormInput //form component
                        label="Password"
                        name="password"
                        type="password"
                        placeholder="enter password"
                        onchange={this.handleChange}
                      />
                    </li>

                    <li>
                      <input
                        type="checkbox"
                        onChange={this.handleChange}
                        required
                      />
                      Remember Me
                    </li>
                    <p style={{ color: "red" }}>{this.state.error}</p>
                    <li>
                      <input type="submit" defaultValue="Log In" />
                      <a onClick={this.openForgetPassDialogBox}>
                        Forgot Password
                      </a>
                    </li>
                  </ul>
                </form>
                <div className="addtnal_acnt">
                  I do not have any account yet.
                  <a href>
                    <Link to="/">Create My Account Now !</Link>
                  </a>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    );
  }
}
export default withRouter(Login);
