const initialState={
    data:{},
    isLoggedIn:false
}
const reducer =(state=initialState,action)=>{
    console.log("in reducer ",action)
    switch(action.type){
        case 'SET_LOGED_IN_USER_DATA':
        return {
            ...state,
            data:action.payload.all,
            isLoggedIn:true
        }
        case 'LOGGED_OUT':
            return {
                ...state,
                isLoggedIn:false
            }
        default:
            return {...state}
    }

}
export default reducer;
