import React, { useState } from "react";
import { BrowserRouter, Route, Switch } from "react-router-dom";
import { Redirect } from "react-router-dom";
import "./App.css";
import Landing_page from "./screens/landingPage";
import ScrollToTop from "./components/ScrollToTop";
import Timeline from "./screens/timeline/Timeline";
import ForgotPassword from "./screens/ResetPassword";

import SinglePOst from "./screens/timeline/singlepost";

function App(props) {
  // const [redirect, setRedirect] = useState(true);

  const token = localStorage.getItem("id");

  return (
    <BrowserRouter>
      <ScrollToTop>
        <Switch>
        <Route
              exact
              path="/timeline"
              render={() => {
                return (
                  token != null ? (
                  <Redirect to="/timeline" />
                ) : (
                  <Redirect to="/" />
                )
                )
              }}

            />
          {console.log(token)}
          <Route exact path="/reset/:token" component={ForgotPassword} />
          <Switch>


            <Route  path="/" component={Landing_page} />
            <Route  path="/timeline" component={Timeline} />
            <Route path="/timeline/:id" component={SinglePOst} />
          </Switch>{" "}
        </Switch>

        {/* <Footer/> */}
      </ScrollToTop>
    </BrowserRouter>
  );
}
export default App;
