import { put, takeLatest,call } from 'redux-saga/effects'
import axios from 'axios'


function getUserData(action){
    return axios.post("http://localhost:3002/auth/login",action.payload)
}
export function* getDataAsync(action) {
  try{
      console.log(action)
       const allData=  yield call(getUserData, action)
        yield put({ type: 'SET_LOGED_IN_USER_DATA',allData})
  }
  catch (error) {
    yield put({ type:"HANDLEERROR", error });
  }
}

export function* watchLoggedInUser(action) {
    yield takeLatest('REQUEST_LOGIN', getDataAsync)
}
